sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          var rn = new GlideDateTime();

		  var od = new GlideRecord('x_58872_needit_needit_task');
		  od.addQuery('due_date', '<=', rn);
		  od.addQuery('state', '<', 3);
		  od.addQuery('assigned_to', current.assigned_to);

		  od.query();

		  template.print('In addition this overdue task ' + (od.getRowCount() - 1) + ' more overdue NeedIt tasks.');

})(current, template, email, email_action, event);
